    private void jButton1ActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_jButton1ActionPerformed
        try {
            String nombre = nombreEntrada.getText();
            String apellidos = apellidosEntrada.getText();
            String correo = correoEntrada.getText();
            int edad = Integer.parseInt(edadEntrada.getText());
            String estado = "";

            if (estudianteEntrada.isSelected()) {
                estado = estudianteEntrada.getText();
            }
            if (profesorEntrada.isSelected()) {
                estado = profesorEntrada.getText();
            }

            String pais = paisEntrada.getName();

            Usuario nuevoUser = new Usuario(nombre, apellidos, correo, edad, estado, pais);
            
            dialogo.setText(nuevoUser.toString());
            
        } catch (Exception e) {
            System.out.println("Error en el formulario");
        }


    }//GEN-LAST:event_jButton1ActionPerformed

